package com.vjs.StringExamples;

public class Static_defaultvalues {
	static int i;
	static byte b;
	static char c;
	static short sh;
	static long l;
	static float f;
	static double d;
	static boolean bl;
	static String s;
	static Integer in;
	static Character ch;
	static int[] ab;
	static Static_defaultvalues g;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(Static_defaultvalues.i+"for int");
		System.out.println(Static_defaultvalues.b+"for byte");
		System.out.println(Static_defaultvalues.c+"for char");
		System.out.println(Static_defaultvalues.sh+"for short");
		System.out.println(Static_defaultvalues.l+"for long");
		System.out.println(Static_defaultvalues.f+"for float");
		System.out.println(Static_defaultvalues.d+"for double");
		System.out.println(Static_defaultvalues.bl+"for boolean");
		System.out.println(Static_defaultvalues.s+"for string");
		System.out.println(Static_defaultvalues.in+"for Integer");
		System.out.println(Static_defaultvalues.ch+"for Character");
		
		System.out.println(Static_defaultvalues.ab+"for int array");
		System.out.println(Static_defaultvalues.g+"for int currentclass");
		

	}

}
